// Challenge 1 solution
const friends = [
  {
    name: "sam",
    score: 20,
    isMeet: false,
  },
  {
    name: "john",
    score: 100,
    isMeet: false,
  },
  {
    name: "Jim",
    score: 15,
    isMeet: false,
  },
  {
    name: "ali",
    score: 5,
    isMeet: false,
  },
];
export default function tobiesFriends(n, scores) {
  // sorthe friends list by thier score DESC
  n.sort((a, b) => parseFloat(b.score) - parseFloat(a.score));

  for (let i = 0; i < n.length; i++) {
    if (n[i].isMeet) {
      n[i].score = n[n.length - 1].score - 1;
    } else {
      n[i].isMeet = true;
      n[i].score = n[n.length - 1].score - 1;
      addScore(n, i);
    }
  }
  function addScore(FriendsList, index) {
    for (let i = index + 1; i < FriendsList.length; i++) {
      FriendsList[i].score++;
    }
  }
}
tobiesFriends(friends, 100);
